    const { RUE_RECORD_ID } = ActiveRecord$Base;
    if (!limit) limit = 1;
    // @ts-expect-error
    const _this = this as ActiveRecord$Relation<T>;
    // @ts-expect-error
    if (!_this._currentScopeFn) _this._currentScopeFn = () => Promise.resolve(_this.records);
    // @ts-expect-error
