reverseOrder<T extends ActiveRecord$Base>(): ActiveRecord$Relation<T> {
    // @ts-expect-error
    const _this = this as ActiveRecord$Relation<T>;
    // @ts-expect-error
    _this.superThen(([holder]) => {
      const orderParams = holder.scopeParams['order'];
