        Object.keys(orderParams).forEach((propName) => {
          const direction = orderParams[propName];
          if (['desc', 'DESC'].includes(direction)) {
            holder.scopeParams['order'][propName] = 'asc';
          } else if (['asc', 'ASC'].includes(direction)) {
