    if (scopeMethods.length === 0) {
      throw errObj({
        code: ErrCodes.ARGUMENT_IS_INVALID,
        message: `'unscope()' must contain arguments.`,
      });
    } else if (isSuperset(SCOPE_METHODS, scopeMethods)) {
      scopeMethods.forEach((scopeMethod) => {
        // @ts-expect-error
