export class ActiveSupport$Base extends ActiveSupport$Impl {
  static inspect(instance: any): string {
    const klassName = instance.constructor.name;
    let sorted = {};

    const keys = Object.keys(instance).sort();
    keys.forEach(function (key) {
      sorted[key] = this[key];
