export class ActiveModel$Error extends Error implements t.ErrObj {
  private _namespace?: string;
  private _code?: string;
  private _message?: string;

  get namespace(): string {
    return this._namespace || '';
  }

  set namespace(val: string) {
    this._namespace = val;
  }

  get code(): string {
    return this._code || '';
  }

  set code(val: string) {
    this._code = val;
  }

  get message(): string {
    return this._message || '';
  }

  set message(val: string) {
    this._message = val;
  }
}

export interface ErrObj extends Error {
  code: string;
  namespace: string;
  message: string;
}

export class ActiveModel$Base extends ActiveModel$Impl {
  public errors: Validations$Errors;

  constructor(data: t.Params = {}) {
    super();
    this.errors = {};
    if (!data) data = {};
    Object.keys(data).forEach((key) => {
      (this as any)[key] = data[key];
    });
  }

  static get objType(): t.ObjType {
    return 'model';
  }

  toObj(opts?: { flat: boolean }): t.Params {
    opts = opts == undefined ? { flat: false } : opts;
    const instance = Object.create(this);
    const obj = Object.assign({}, instance.__proto__);
    return opts.flat ? Object.assign(flatten(obj), { errors: obj.errors }) : obj;
  }

  inspect(): string {
    return Support.inspect(this);
  }
}

