      let fnVal;
      fnArray.forEach((fn) => {
        if (fn.length == 2) {
          fnVal = fn(propVal, this);
        } else {
          fnVal = fn(propVal);
        }

        if (Array.isArray(fnVal)) {
          maybeErrors.push(...fnVal);
        }
